from collections import defaultdict
import heapq

def minimum_travel_time(n, m, a, b, roads):
    graph = defaultdict(list)
    for u, v, c in roads:
        graph[u].append((v, c))
        graph[v].append((u, c))

    def dijkstra(start):
        dist = [float('inf')] * (n + 1)
        dist[start] = 0
        queue = [(0, start)]
        while queue:
            d, u = heapq.heappop(queue)
            if d > dist[u]:
                continue
            for v, c in graph[u]:
                if dist[v] > d + c:
                    dist[v] = d + c
                    heapq.heappush(queue, (dist[v], v))
        return dist

    # Find the minimum spanning tree using Prim's algorithm
    def prim():
        mst_set = [False] * (n + 1)
        key = [float('inf')] * (n + 1)
        key[1] = 0
        for _ in range(n):
            u = min(range(1, n + 1), key=lambda x: key[x] if not mst_set[x] else float('inf'))
            mst_set[u] = True
            for v, c in graph[u]:
                if not mst_set[v] and key[v] > c:
                    key[v] = c
        return sum(key[1:])

    # Calculate the minimum time required to travel from 1 to p
    min_time = dijkstra(1)
    min_mst_time = prim()
    result = [0] * n
    for p in range(1, n + 1):
        result[p - 1] = min_time[p] if min_time[p] <= min_mst_time else -1
    return result

# Read input and call the function
n, m, a, b = map(int, input().split())
roads = [tuple(map(int, input().split())) for _ in range(m)]
print(*minimum_travel_time(n, m, a, b, roads))

def distinct_names(n, names):
