def lucky_permutation(n):
    if n % 4 in [2, 3]:
        return -1
    else:
        perm = [0] * n
        for i in range(1, n // 4 + 1):
            perm[i - 1] = 2 * i
            perm[2 * i - 1] = n - 2 * i + 1
            perm[n - 2 * i] = 2 * i - 1
            perm[n - 2 * i + 1] = n - 2 * i + 2
        if n % 4 == 1:
            perm[n // 2] = n // 2 + 1
        return ' '.join(map(str, perm))

