def min_cost_to_equal(a, b):
    n = len(a)
    cost = 0
    ones_a = []
    ones_b = []
    zeros_a = []
    zeros_b = []

    for i in range(n):
        if a[i] != b[i]:
            if a[i] == '1':
                ones_a.append(i)
            else:
                zeros_a.append(i)
            if b[i] == '1':
                ones_b.append(i)
            else:
                zeros_b.append(i)

    # Match 1s with 1s and 0s with 0s where possible
    while ones_a and ones_b:
        cost += abs(ones_a.pop() - ones_b.pop())
    while zeros_a and zeros_b:
        cost += abs(zeros_a.pop() - zeros_b.pop())

    # Flip the remaining unmatched bits
    cost += len(ones_a) + len(zeros_a)

    return cost

# Read input and call the function
n = int(input())
a = input().strip()
b = input().strip()
print(min_cost_to_equal(a, b))

def covering_graph_with_cycles(n):
    if n % 2 == 1:
        return -1
    cycles = []
    for i in range(1, n+1):
        for j in range(i+1, n+1):
            if (i + j) % 2 == 0:
                cycles.append((3, i, j, 1))
            else:
                cycles.append((3, i, j, n))
    return cycles

